import React, { useState,useEffect } from 'react'
import axios from "./axios"
import "./Row.css";
import Youtube from "react-youtube"
const baseURL="https://image.tmdb.org/t/p/original/";
function Row({title,fetchURL,isLargeRow}) {
    const [movies, setMovies] = useState([])
    
     useEffect(() => {
       async function fetchData(){
           const request=await axios.get(fetchURL)
        //    console.log(request)
           setMovies(request.data.results)
           return request
       }
       fetchData()
      
    }, [fetchURL])
    const opts = {
        height: '390',
        width: '640',
        playerVars: {
          // https://developers.google.com/youtube/player_parameters
          autoplay: 1,
        },
      };
    return (
        <div className="row">
            <h1>{title}</h1>
            <div className={`row_posters ${isLargeRow && "row_poster_large"}`}>
                {movies.map(movie=>(
                    <img className="row-poster"  src={`${baseURL}${isLargeRow ?  movie.poster_path : movie.backdrop_path}`} alt={movie.name}></img>
                ))}
            </div>
            <Youtube videoId={trailerURL} opts={opts}></Youtube>
        </div>
    )
}

export default Row
