import axios from './axios'
import React, { useEffect, useState } from 'react'
import request from "./api"
const baseURL="https://image.tmdb.org/t/p/original/";
function Banner() {
    const [movies, setMovies] = useState([])
    useEffect(() => {
        async function fetchData(){
            const result=await axios.get(request.fetchNetflixOriginals)
            setMovies(result.data.results[Math.floor(Math.random(result.data.results.length -1))])
            console.table(Math.random(result.data.results.length -1))
        }
        fetchData()

    }, [])
    
    return (
        
            <header className="banner"
            style={{
                backgroundSize:"cover",
                backgroundImage:`url(https://image.tmdb.org/t/p/original/${movies?.backdrop_path})`,
                backgroundPosition:"center center",
                
                }}>
            <div className="banner_contents">
            <h1>{movies.name}</h1>
            <div className="banner_buttons">
            <button>Play</button>
            <button>My List</button>
            </div>
            <div className="banner_description">
                <h1>{movies.overview}</h1>
            </div>
            </div>
            {/* <img src={ `${baseURL}${movies.poster_path}`}></img> */}
            </header>
        
    )
}
export default Banner
